import CheckListItem from "./CheckListItem";

// A small style object for the table and cells for resemblance to the image
const tableStyle = {
  width: '100%',
  borderCollapse: 'collapse',
  marginTop: '10px',
  marginBottom: '20px',
  textAlign: 'left'
};

const cellStyle = {
  border: '1px solid black',
  padding: '8px',
  fontWeight: 'bold', // For the labels in the first column
  width: '25%' // Set width for a 4-column layout
};

const valueCellStyle = {
    border: '1px solid black',
    padding: '8px',
    fontWeight: 'normal',
    width: '25%'
};

export default function FlightDetails({ flight, onClose }) {
  // Helper to format date/time
  const formatDate = (timestamp) => 
    timestamp ? new Date(timestamp).toLocaleDateString() : '____/____/2023';

  const formatDateTime = (timestamp) => 
    timestamp ? new Date(timestamp).toLocaleString() : '';
    
  // Format the date for the display header
  const headerDate = formatDate(flight.date);

  return (
    <div style={{ padding: 20 }}>

      <h1 style={{ textDecoration: 'underline', marginBottom: '30px' }}>PRE-FLIGHT CHECKLIST:</h1>

      <div style={{ display: 'flex', justifyContent: 'space-between', marginBottom: '10px', fontWeight: 'bold', fontSize: '1.1em' }}>
        <p>FLIGHT NUMBER:{flight.flightNumber}</p>
        <p>DATE: {headerDate}</p>
      </div>

      <table style={tableStyle}>
        <tbody>
          <tr>
            <td style={cellStyle}>Filed By</td>
            <td style={valueCellStyle}>{flight.filedBy}</td>
            <td style={cellStyle}>Filing Time</td>
            <td style={valueCellStyle}>{formatDateTime(flight.filingTime)}</td>
          </tr>
          <tr>
            <td style={cellStyle}>Departure Location</td>
            <td style={valueCellStyle}>{flight.departureLocation}</td>
            <td style={cellStyle}>Departure Time</td>
            <td style={valueCellStyle}>{formatDateTime(flight.departureTime)}</td>
          </tr>
          <tr>
            <td style={cellStyle}>Arrival Location</td>
            <td style={valueCellStyle}>{flight.arrivalLocation}</td>
            <td style={cellStyle}>Est. Arrival Time</td>
            <td style={valueCellStyle}>{formatDateTime(flight.estimatedArrivalTime)}</td>
          </tr>
        </tbody>
      </table>

      <h3 style={{ marginTop: '30px' }}>Checklist Items</h3>
      {flight.checklist.map((item) => (
        <CheckListItem
          key={item._id}
          flightId={flight._id}
          item={item}
        />
      ))}

      <div style={{ marginTop: '20px' }}>
        <button onClick={onClose}>Close</button>
      </div>
    </div>
  );
}
