import { useState } from "react";
import API from "../api";

export default function FlightForm({ onCreated }) {
  const [flightNumber, setFlightNumber] = useState("");
  const [filedBy, setFiledBy] = useState("");
  const [filingTime, setFilingTime] = useState("");
  const [departureLocation, setDepartureLocation] = useState("");
  const [departureTime, setDepartureTime] = useState("");
  const [arrivalLocation, setArrivalLocation] = useState("");
  const [estimatedArrivalTime, setEstimatedArrivalTime] = useState("");

  const createFlight = async () => {
    await API.post("/", {
      flightNumber,
      filedBy,
      filingTime,
      departureLocation,
      departureTime,
      arrivalLocation,
      estimatedArrivalTime
    });
    // Reset form
    setFlightNumber("");
    setFiledBy("");
    setFilingTime("");
    setDepartureLocation("");
    setDepartureTime("");
    setArrivalLocation("");
    setEstimatedArrivalTime("");
    onCreated();
  };

  return (
    <div style={{ marginBottom: 30 }}>
      <h2>Create Flight</h2>
      <div style={{ display: "flex", flexDirection: "column", gap: "10px", maxWidth: "400px" }}>
        <input
          type="text"
          placeholder="Flight Number"
          value={flightNumber}
          onChange={(e) => setFlightNumber(e.target.value)}
        />
        <input
          type="text"
          placeholder="Filed By"
          value={filedBy}
          onChange={(e) => setFiledBy(e.target.value)}
        />
        <input
          type="datetime-local"
          placeholder="Filing Time"
          value={filingTime}
          onChange={(e) => setFilingTime(e.target.value)}
        />
        <input
          type="text"
          placeholder="Departure Location"
          value={departureLocation}
          onChange={(e) => setDepartureLocation(e.target.value)}
        />
        <input
          type="datetime-local"
          placeholder="Departure Time"
          value={departureTime}
          onChange={(e) => setDepartureTime(e.target.value)}
        />
        <input
          type="text"
          placeholder="Arrival Location"
          value={arrivalLocation}
          onChange={(e) => setArrivalLocation(e.target.value)}
        />
        <input
          type="datetime-local"
          placeholder="Estimated Arrival Time"
          value={estimatedArrivalTime}
          onChange={(e) => setEstimatedArrivalTime(e.target.value)}
        />
        <button onClick={createFlight}>Create Flight</button>
      </div>
    </div>
  );
}
