import { useState } from "react";
import API from "../api";

export default function FlightForm({ onCreated }) {
  // Form state using an object for easier management
  const [formData, setFormData] = useState({
    flightNumber: "",
    filedBy: "",
    filingTime: "",
    departureLocation: "",
    departureTime: "",
    arrivalLocation: "",
    estimatedArrivalTime: ""
  });

  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState("");

  // Handle form input changes
  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({ ...prev, [name]: value }));
  };

  // Form validation function
  const validateForm = () => {
    return Object.values(formData).every((field) => field.trim() !== "");
  };

  // Create flight and reset form
  const createFlight = async () => {
    if (!validateForm()) {
      setError("Please fill in all the fields.");
      return;
    }

    setIsLoading(true);
    setError("");

    try {
      await API.post("/", formData);
      // Reset form state after successful creation
      setFormData({
        flightNumber: "",
        filedBy: "",
        filingTime: "",
        departureLocation: "",
        departureTime: "",
        arrivalLocation: "",
        estimatedArrivalTime: ""
      });
      onCreated(); // Notify parent component that the flight was created
    } catch (err) {
      setError("Failed to create flight. Please try again.");
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <div style={{ marginBottom: 30 }}>
      <h2>Create Flight</h2>
      {error && <p style={{ color: "red" }}>{error}</p>}
      <div style={{ display: "flex", flexDirection: "column", gap: "10px", maxWidth: "400px" }}>
        <input
          type="text"
          name="flightNumber"
          placeholder="Flight Number"
          value={formData.flightNumber}
          onChange={handleInputChange}
        />
        <input
          type="text"
          name="filedBy"
          placeholder="Filed By"
          value={formData.filedBy}
          onChange={handleInputChange}
        />
        <input
          type="datetime-local"
          name="filingTime"
          value={formData.filingTime}
          onChange={handleInputChange}
        />
        <input
          type="text"
          name="departureLocation"
          placeholder="Departure Location"
          value={formData.departureLocation}
          onChange={handleInputChange}
        />
        <input
          type="datetime-local"
          name="departureTime"
          value={formData.departureTime}
          onChange={handleInputChange}
        />
        <input
          type="text"
          name="arrivalLocation"
          placeholder="Arrival Location"
          value={formData.arrivalLocation}
          onChange={handleInputChange}
        />
        <input
          type="datetime-local"
          name="estimatedArrivalTime"
          value={formData.estimatedArrivalTime}
          onChange={handleInputChange}
        />
        <button onClick={createFlight} disabled={isLoading || !validateForm()}>
          {isLoading ? "Creating..." : "Create Flight"}
        </button>
      </div>
    </div>
  );
}
